svg.script:
var state = "";
var test = 0;

function testcase(h, m, result){
    r = update(h, m);
    if ( r == result)
        console.log(h,":",m," OK");
    else {
        console.log(h,":",m," FAIL ",r);
        throw(h,":",m," FAIL ",r);
    }
}

function check(){
    testcase( 0,  0, "jetzisch0");
    testcase(10,  0, "jetzisch10fai2scho");
    testcase(11, 35, "jetzischsfenfnoch300");
    testcase(11, 25, "jetzischsfenfbis300");
    testcase(11, 26, "wargenaufenfbis300");
    testcase(11, 27, "wargenaufenfbis300");
    testcase(12,  0, "jetzisch0fai");
    testcase(12,  1, "wargenau0");
    testcase( 3,  4, "gleiischsfenfnoch3");
    testcase( 3,  6, "wargenaufenfnoch3");
    testcase( 5, 29, "jetzischsno306");
    testcase( 6, 37, "wargenaufenfnoch307");
    testcase( 6, 53, "gleiischsfenfbis7");
    testcase( 7, 18, "gleiischsfenfnoch158");
    testcase( 7,  5, "jetzischsfenfnoch7");
    testcase( 8, 24, "gleiischsfenfbis309");
    testcase( 9,  6, "wargenaufenfnoch9");
    testcase( 9,  7, "wargenaufenfnoch9");
    testcase( 9,  8, "gleiischsfenfbis1510");
    testcase( 9, 32, "wargenau3010sekonda");
    testcase( 9, 43, "jetzischsno4510");
    testcase( 9, 45, "jetzisch4510");
    testcase(10, 42, "wargenaufenfbis4511");

    alert("all good");
}

function light(id){
    try {
        document.getElementById(id).style.fill = "#cc66ff";
        if (test)
            state=state+id;
    }
    catch(err) {
        console.log("Error in light() id =",id);
    }
}

function update(h, m){
    var p = document.getElementsByTagName("text");
    for (var i=0; i < p.length; i++){
        p[i].style.fill="#dddddd";
    }

    if (h == -1 && m == -1){
        h = new Date().getHours()

        h = h % 12;
        m = new Date().getMinutes();
    }


    d = Math.floor(m/15);
    e = m % 15;
    z = 0;

    switch (e){
        case 0: // 15 30 45
            z = 1;
            light("jetz");
            light("isch");
            break;
        case 1:
        case 2: // 17 32 47
            z = 1;
            light("war");
            light("genau");
            break;
        case 3:
        case 4: // 19 34 49
            z = 1;
            light("glei");
            light("isch");
            light("s");
            light("fenf");
            light("noch");
            break
        case 5: // 20 35 50
            z = 1;
            light("jetz");
            light("isch");
            light("s");
            light("fenf");
            light("noch");
            break;
        case 6:
        case 7:
            light("war");
            light("genau");
            light("fenf");
            light("noch");
            break;
        case 8: // 23
        case 9:
            z = 1;
            light("glei");
            light("isch");
            light("s");
            light("fenf");
            light("bis");
            break
        case 10: // 25 40 55
            z = 1;
            light("jetz");
            light("isch");
            light("s");
            light("fenf");
            light("bis");
            break
        case 11:
        case 12:
            z = 1;
            light("war");
            light("genau");
            light("fenf");
            light("bis");
            break
        case 13:
        case 14:
            z = 1;
            light("jetz");
            light("isch");
            light("s");
            light("no");

            //light("glei");
            //light("isch");
            //light("s");
            break
    }

    n = ((Math.floor((m-6)/15) + z) * 15) % 60;
    if (n != 0)
        light(n.toString());

    if (m >= 8)
        h = h + 1;

    light((h%12).toString());


    if (m == 32)
        light("sekonda");

    if (h == 12 && m == 0)
        light("fai");

    if (h == 10 && m == 0){
        light("fai2");
        light("scho");
    }

    a = state;
    state = "";
    return a;
}


if (!test){
    window.onload = function() {
        update(-1, -1);
        var myVar = setInterval(function(){ update(-1, -1) }, 1000);
    }
} else
    window.onload = function() { check(); }
